model.metadata:
            SET NAMESPACE 'http://teiid.org/rest' AS REST;
                CREATE VIRTUAL PROCEDURE GetEmployees(IN name string)
                RETURNS (json clob) OPTIONS (UPDATECOUNT 0, "REST:METHOD" 'GET', "REST:URI" 'get_employees')

                AS
                BEGIN
                    execute immediate
                    'SELECT JSONOBJECT(
                        JSONARRAY_AGG(
                            JSONOBJECT(
                              id,
                              name
                            )
                        ) as "data"
                    ) as json FROM(
                        SELECT id,
							name
                        FROM myDB.Employee
                    ) A';
                END
